export class AddTopicComponent {
    constructor(topicService, snackbar, route, activityService, router) {
        this.topicService = topicService;
        this.snackbar = snackbar;
        this.route = route;
        this.activityService = activityService;
        this.router = router;
        this.topicForm = new FormGroup({
            topic: new FormControl("", [
                Validators.required, Validators.minLength(5), this.validateTopicname()
            ]),
            // topic: new FormControl('', {
            //   validators: [Validators.required, Validators.minLength(5)],
            //   asyncValidators: this.uniqueEmailValidator(),
            //   updateOn: 'blur',
            // }),
            ageGroup: new FormControl("", [
                Validators.required
            ]),
            language: new FormControl("", [
                Validators.required
            ]),
            country: new FormControl("", [
                Validators.required
            ]),
            grade: new FormControl("", [
                Validators.required
            ]),
            noOfQuestions: new FormControl("", [
                Validators.required
            ]),
            time: new FormControl("", [
                Validators.required
            ])
        });
        this.searchForm = new FormGroup({
            search: new FormControl("", [
                Validators.required
            ])
        });
        this.topic = [];
        this.searchText = '';
        this.selectedAge = '';
        this.age = [];
        this.language = [];
        this.country = [];
        this.grade = [];
        this.type = [];
        this.topics = [];
    }
    ngOnInit() {
        this.subject = this.route.snapshot.paramMap.get('id');
        this.topicService.getTopicBySubject(this.subject)
            .subscribe(res => {
            this.topic = res;
            console.log('response', res);
        }, err => {
            console.log(err);
        });
        this.activityService.getGeGroup()
            .subscribe(data => {
            this.age = data;
            console.log(data);
        });
        this.activityService.getAllLanguage()
            .subscribe(langData => {
            this.language = langData;
            console.log(langData);
        });
        this.activityService.getCountry()
            .subscribe(countryData => {
            this.country = countryData;
            console.log(countryData);
        });
        this.activityService.getGrade()
            .subscribe(gradeData => {
            this.grade = gradeData;
            console.log(gradeData);
        });
        this.activityService.getQuestionType()
            .subscribe(typeData => {
            this.type = typeData;
            console.log(typeData);
        });
    }
    onSubmit() {
        console.log(this.topicForm.value);
        this.subId = this.route.snapshot.paramMap.get('id');
        this.topicService.addTopic(this.topicForm.value, this.subId, this.topicForm.value.ageGroup)
            .subscribe(res => {
            this.snackbar.open('Your topic has been posted', 'Ok', {
                duration: 5000,
                panelClass: ['blue-snackbar']
            });
            this.getTopicByAgeId(this.topicForm.value.ageGroup);
            this.topicForm.reset();
            localStorage.setItem('remainingQuestions', '0');
            this.router.navigate([`/material/type/${res._id}`]);
        });
    }
    uniqueEmailValidator() {
        return (control) => {
            return this.topicService.topicNameCheck(control.value).pipe(map((exists) => (exists ? { emailExists: true } : null)));
        };
    }
    validateTopicname() {
        return (control) => {
            return this.topicService.topicNameCheck(control)
                .subscribe(res => {
                this.topicName = res;
                console.log(control.value);
                if (this.topicName.topic === control.value) {
                    return { 'alreadyExist': true };
                }
                else {
                    return null;
                }
            }, (error) => {
                console.log(error);
            });
        };
    }
    getTopicByAgeId(ageId) {
        this.subId = this.route.snapshot.paramMap.get('id');
        this.topicService.getTopicByAgeId(this.subId, ageId)
            .subscribe(res => {
        });
    }
    searchData(event) {
        var text = event.target.value;
        this.topics = this.topic.filter(x => {
            return (x.topic.toLowerCase()).includes(text.toLowerCase());
        });
    }
    onDelete(id) {
        this.topicService.deleteTopic(id).subscribe(res => {
            this.ngOnInit();
            this.snackbar.open(" Your Topic has been Deleted", "Ok", {
                duration: 5000,
                panelClass: ['blue-snackbar']
            });
        });
    }
}
